	$GLOBALS['IndexSF']=Array();
	
	function findSF_Indexes($fp,$first=0,$last=3025731,$level=0)
	{
		$mid = (int)($first + ($last - $first)/2);
		fseek($fp,$mid*8);
		$GLOBALS['IndexSF'][$mid]=my_unpack(fread($fp,4));
		if($level<9)
		{
			findSF_Indexes($fp,$first,$mid,$level+1);
			findSF_Indexes($fp,$mid + 1,$last,$level+1);
		}
	}
